sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var getGroupList = new GlideAjax('getAssignedToGroupList');
	getGroupList.addParam('sysparm_name', 'getGroups');
	getGroupList.getXMLAnswer(showFieldMessage);

	function showFieldMessage(response){
		g_form.showFieldMsg('assigned_to' , answer);
	}
}
